    private void navigateButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_navigateButtonActionPerformed
      
        String  originRegion =   originRegionTextField.getText();
        String destinationRegion =   destinationRegionTextField.getText();
        String origin = originTextField.getText();
        String destination = destinationTextField.getText();
        System.out.println("起点所在城市 origin_region: " + originRegion);
        System.out.println("目标所在城市 destination_region: " + destinationRegion);
        System.out.println("起点 origin:" + origin);
        System.out.println("目的地 destination: " + destination);
        
         resultListModel = DirectionUtil.navigate(origin, destination, originRegion, destinationRegion);

         routesList.setModel(resultListModel);
       // System.out.println("导航路径 The result is:\n" + result);
        destinationTextField.requestFocusInWindow();       
    }//GEN-LAST:event_navigateButtonActionPerformed
    
